} fn main() { let my_number = 10; // åšå‡ºä¸‰å€‹é–‰åŒ… let mut doubles = returns_a_closure("double"); let mut triples = returns_a_closure("triple"); let mut quadruples = returns_a_closure("quadruple"); doubles(my_number); triples(my_number); quadruples(my_number);
} ä¸‹é¢æ˜¯å€‹æœ‰é»é•·çš„ç¯„ä¾‹ã€‚è®“æˆ‘å€‘æƒ³åƒåœ¨éŠæˆ²ä¸­ï¼Œä½ çš„è§’è‰²é¢å°çš„æ˜¯æ™šä¸Šæ¯”è¼ƒå¼·çš„æ€ªç‰©ã€‚æˆ‘å€‘å¯ä»¥åšå‡ºå« TimeOfDay çš„åˆ—èˆ‰ä¾†è¨˜éŒ„ä¸€å¤©çš„æƒ…æ³ã€‚ä½ çš„è§’è‰²å«è¥¿è’™ï¼Œæœ‰å€‹å« character_fear æ˜¯ f64 çš„æ•¸å­—ã€‚å®ƒæ™šä¸Šä¸Šå‡ã€ç™½å¤©ä¸‹é™ã€‚æˆ‘å€‘å°‡å¯«å€‹å« change_fear çš„å‡½å¼ä¾†æ”¹è®Šä»–çš„ææ‡¼ï¼Œä½†ä¹Ÿæœƒåšå…¶ä»–äº‹æƒ…ï¼Œå¦‚å¯«è¨Šæ¯ã€‚å®ƒå¤§æ¦‚æœƒæ˜¯é€™æ¨£ï¼š enum TimeOfDay { // åªæ˜¯å–®ç´”çš„åˆ—èˆ‰ Dawn, Day, Sunset, Night,
} fn change_fear(input: TimeOfDay) -> impl FnMut(f64) -> f64 { // é€™å€‹å‡½å¼æ¥å— TimeOfDay. å›å‚³é–‰åŒ…. // æˆ‘å€‘ç”¨ impl FnMut(64) -> f64 ä¾†èªªæ˜å®ƒéœ€è¦ // æ”¹è®Šå€¼, ä¸¦ä¸”ä¹Ÿçµ¦å›ä¸€æ¨£çš„å‹åˆ¥. use TimeOfDay::*; // æ‰€ä»¥æˆ‘å€‘åªè¦å¯« Dawnã€Dayã€Sunsetã€Night // è€Œä¸æ˜¯ TimeOfDay::Dawnã€TimeOfDay::Day ç­‰ç­‰. match input { Dawn => |x| { // é€™å°±æ˜¯æˆ‘å€‘ä¹‹å¾Œæœƒçµ¦äºˆçš„è®Šæ•¸ character_fear println!("The morning sun has vanquished the horrible night. You no longer feel afraid."); println!("Your fear is now {}", x * 0.5); x * 0.5 }, Day => |x| { println!("What a nice day. Maybe put your feet up and rest a bit."); println!("Your fear is now {}", x * 0.2); x * 0.2 }, Sunset => |x| { println!("The sun is almost down! This is no good."); println!("Your fear is now {}", x * 1.4); x * 1.4 }, Night => |x| { println!("What a horrible night to have a curse."); println!("Your fear is now {}", x * 5.0); x * 5.0 }, }
} fn main() { use TimeOfDay::*; let mut character_fear = 10.0; // è¥¿è’™å¾ 10 é–‹å§‹ let mut daytime = change_fear(Day); // é€™è£¡åšå››å€‹é–‰åŒ…åœ¨æ¯æ¬¡æˆ‘å€‘æƒ³æ”¹è®Šè¥¿è’™çš„ææ‡¼æ™‚å»å‘¼å«. let mut sunset = change_fear(Sunset); let mut night = change_fear(Night); let mut morning = change_fear(Dawn); character_fear = daytime(character_fear); // å°è¥¿è’™çš„ææ‡¼å‘¼å«é–‰åŒ…. å®ƒå€‘çµ¦å‡ºè¨Šæ¯ä¸¦æ”¹è®Šææ‡¼æ•¸å€¼. // åœ¨ç¾å¯¦ç”Ÿæ´»æˆ‘å€‘æœƒæœ‰ Character çµæ§‹é«”ä¸¦æŠŠå®ƒç•¶æ–¹æ³•ç”¨, // åƒé€™æ¨£: character_fear.daytime() character_fear = sunset(character_fear); character_fear = night(character_fear); character_fear = morning(character_fear);
